import React, { useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./GenerateCard.css";

const GenerateCard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const cardData = location.state?.formData || {
    snfNo: "N/A",
    cardHolderName: "Not Available",
    mobile: "Not Available",
    address: "Not Available",
    familyDetails: []
  };

  // Auto-save card data (no loading states)
  useEffect(() => {
    const storeCard = async () => {
      try {
        await fetch("http://localhost:5000/store-card", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ cardData }),
        });
      } catch (error) {
        console.error("Save failed (check console)");
      }
    };
    storeCard();
  }, []);

  return (
    <div className="card-wrapper">
      <h2 className="title">Happy Doctors Consultancy Card</h2>
      
      <div className="card-container">
        {/* Front Side */}
        <div className="card front">
          <div className="card-header">
            <h3>Card No: {cardData.snfNo}</h3>
          </div>
          <div className="card-content">
            <p><strong>Name:</strong> {cardData.cardHolderName}</p>
            <p><strong>Phone:</strong> {cardData.mobile}</p>
            <p><strong>Address:</strong> {cardData.address}</p>
          </div>
        </div>

        {/* Back Side */}
        <div className="card back">
          <div className="card-header">
            <h4>Family Members</h4>
          </div>
          <div className="card-content">
            {cardData.familyDetails.map((member, index) => (
              <p key={index}>
                {member.name} ({member.age}, {member.relation})
              </p>
            ))}
          </div>
        </div>
      </div>

      <button className="home-btn" onClick={() => navigate("/")}>
        üè† Go to Home
      </button>
    </div>
  );
};

export default GenerateCard;
