import React, { useState } from "react";
import { auth, provider, signInWithPopup, signOut } from "../firebase";
import { useNavigate } from "react-router-dom";
import logo from "../assets/lgs.jpg";

const GoogleLogin = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`Welcome ${result.user.displayName}`);
    } catch (error) {
      console.error("Google Sign-In Error:", error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      alert("Logged out successfully!");
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  return (
    <div style={styles.container}>
      {user ? (
        <div style={styles.profile}>
          <img src={user.photoURL} alt="Profile" style={styles.avatar} />
          <h3>{user.displayName}</h3>
          <p>{user.email}</p>
          <div style={styles.buttonGroup}>
            <button onClick={() => navigate("/healthcard")} style={styles.cardButton}>
              Happy Doctors Card
            </button>
            <button onClick={handleLogout} style={styles.button}>Logout</button>
          </div>
        </div>
      ) : (
        <div style={styles.container}>
        {/* Left Side - Logo */}
        <img src="/lgs.jpg" alt="Logo" style={styles.logo} />
      
        {/* Center Content */}
        <div style={styles.centerContent}>
          {/* Add any additional center content if needed */}
        </div>
      
        {/* Right Side - Google Sign-in Button */}
        <div style={styles.buttonRight}>
          <button onClick={signInWithGoogle} style={styles.button}>
            Sign in with Google
          </button>
        </div>
      </div>
      

      )}
    </div>
  );
};

const styles = {
  container: { 
    textAlign: "center", // Center all content
    marginTop: "50px", 
  },
  logo: {
    width: "100px", // Adjust the size as needed
    height: "auto",
  },
  centerContent: {
    textAlign: "center",
    flexGrow: 1,
  },
  buttonRight: {
    display: "flex",
    justifyContent: "flex-end", // Move button to the right
    marginRight: "50px",
  },
  buttonGroup: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginTop: "10px",
  },
  button: {
    backgroundColor: "#4285F4",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  cardButton: {
    backgroundColor: "green",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  profile: {
    marginTop: "20px",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    display: "inline-block",
  },
  avatar: { width: "100px", borderRadius: "50%" },
};

export default GoogleLogin;
