import React, { useState } from "react";
import qrCodeImage from "../assets/qr.jpg"; // Import QR code image
import "./Payment.css"; // Import CSS

const Payment = () => {
  const [transactionId, setTransactionId] = useState("");
  const [paymentStatus, setPaymentStatus] = useState(null);

  const handleVerifyPayment = () => {
    if (transactionId.trim() !== "") {
      alert("Please verify this transaction ID manually in your UPI app.");
      setPaymentStatus("pending");
    } else {
      alert("Please enter a valid transaction ID.");
    }
  };

  return (
    <div className="payment-container">
      <h2>Scan & Pay</h2>
      <p>Scan the QR code below to complete the payment.</p>

      <img src={qrCodeImage} alt="UPI QR Code" className="qr-code" />

      <div className="input-section">
        <label>Enter Transaction ID:</label>
        <input
          type="text"
          placeholder="Enter UPI Transaction ID"
          value={transactionId}
          onChange={(e) => setTransactionId(e.target.value)}
        />
        <button onClick={handleVerifyPayment} className="verify-button">
          Verify Payment
        </button>
      </div>

      {paymentStatus === "pending" && (
        <p className="pending-msg">
          âœ… Transaction ID submitted. Please verify manually.
        </p>
      )}
    </div>
  );
};

export default Payment;
