import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./GenerateCard.css"; 

const GenerateCard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const formData = location.state?.formData || JSON.parse(localStorage.getItem("healthCardFormData")); // Get data

  // Calculate the expiration date (1 year from today)
  const issueDate = new Date();
  const expirationDate = new Date();
  expirationDate.setFullYear(expirationDate.getFullYear() + 1);

  return (
    <div className="card-container">
      <h2>ðŸŽ‰ Happy Doctors Consultancy Card ðŸŽ‰</h2>

      {/* Front Side of the Card */}
      <div className="card front">
        <h3>Card No: {formData?.snfNo}</h3>
        <p><strong>Card Holder:</strong> {formData?.cardHolderName}</p>
        <p><strong>Phone:</strong> {formData?.mobile}</p>
        <p><strong>Address:</strong> {formData?.address}</p>
        <p><strong>Issued Date:</strong> {issueDate.toISOString().split("T")[0]}</p>
        <p><strong>Valid Until:</strong> {expirationDate.toISOString().split("T")[0]}</p>
      </div>

      {/* Back Side of the Card */}
      <div className="card back">
        <h4>Family Details</h4>
        <ul>
          {formData?.familyDetails.map((member, index) => (
            <li key={index}>
              {member.name} - {member.age} years old ({member.relation})
            </li>
          ))}
        </ul>
      </div>

      <button onClick={() => navigate("/")}>Go to Home</button>
    </div>
  );
};

export default GenerateCard;
