import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./GenerateCard.css";

const GenerateCard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoading, setIsLoading] = useState(false);
  const [storageStatus, setStorageStatus] = useState(null);
  const [cardData, setCardData] = useState({
    snfNo: "N/A",
    cardHolderName: "Not Available",
    mobile: "Not Available",
    address: "Not Available",
    familyDetails: []
  });

  // Initialize card data
  useEffect(() => {
    const formData = location.state?.formData || 
                    JSON.parse(localStorage.getItem("healthCardFormData"));
    
    if (formData) {
      setCardData(formData);
      storeCardData(formData);
    }
  }, [location.state]);

  const storeCardData = async (formData) => {
    setIsLoading(true);
    setStorageStatus(null);
    
    try {
      const response = await fetch("http://localhost:5000/store-health-card", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          snfNo: formData.snfNo,
          cardHolderName: formData.cardHolderName,
          address: formData.address,
          mobile: formData.mobile,
          familyDetails: formData.familyDetails
        }),
      });

      const data = await response.json();
      if (data.success) {
        setStorageStatus("success");
        // Clear the stored form data after successful submission
        localStorage.removeItem("healthCardFormData");
      } else {
        setStorageStatus("error");
        console.error("Failed to store health card:", data.message);
      }
    } catch (error) {
      setStorageStatus("error");
      console.error("Error storing health card:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // Calculate dates
  const issueDate = new Date();
  const expiryDate = new Date();
  expiryDate.setFullYear(expiryDate.getFullYear() + 1);

  const formatDate = (date) => {
    return date.toISOString().split("T")[0];
  };

  if (!cardData) {
    return (
      <div className="card-wrapper">
        <h2>Error: No card data found</h2>
        <p>Please go back and complete the form again.</p>
        <button className="home-btn" onClick={() => navigate("/")}>
          üè† Go to Home
        </button>
      </div>
    );
  }

  return (
    <div className="card-wrapper">
      <h2 className="title">üéâ Happy Doctors Consultancy Card üéâ</h2>
      
      {isLoading && <div className="loading">Saving your card data...</div>}
      {storageStatus === "success" && (
        <div className="success-message">
          ‚úÖ Card data saved successfully in our system!
        </div>
      )}
      {storageStatus === "error" && (
        <div className="error-message">
          ‚ùå Failed to save card data. Please contact support.
        </div>
      )}

      <div className="card-container">
        {/* Front Side of the Card */}
        <div className="card front">
          <div className="card-header">
            <h3>Card No: {cardData.snfNo}</h3>
          </div>
          <div className="card-content">
            <p><strong>Card Holder:</strong> {cardData.cardHolderName}</p>
            <p><strong>Phone:</strong> {cardData.mobile}</p>
            <p><strong>Address:</strong> {cardData.address}</p>
            <p><strong>Issued Date:</strong> {formatDate(issueDate)}</p>
            <p><strong>Valid Until:</strong> {formatDate(expiryDate)}</p>
          </div>
          <div className="card-footer">
            <p>Present this card when visiting our clinic</p>
          </div>
        </div>

        {/* Back Side of the Card */}
        <div className="card back">
          <div className="card-header">
            <h4>Family Members</h4>
          </div>
          <div className="card-content">
            <ul className="family-list">
              {cardData.familyDetails.map((member, index) => (
                <li key={index}>
                  <strong>{member.name}</strong> - {member.age} years old ({member.relation})
                </li>
              ))}
            </ul>
          </div>
          <div className="card-footer">
            <p>Emergency Contact: {cardData.mobile}</p>
          </div>
        </div>
      </div>

      <div className="action-buttons">
        <button 
          className="print-btn"
          onClick={() => window.print()}
        >
          üñ®Ô∏è Print Card
        </button>
        <button 
          className="home-btn" 
          onClick={() => navigate("/")}
        >
          üè† Go to Home
        </button>
      </div>
    </div>
  );
};

export default GenerateCard;
