import React, { useEffect, useState, useRef } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Barcode from "react-barcode";
import "./GenerateCard.css";
import logo from "../assets/logo.png";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";




const GenerateCard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const cardRef = useRef(null);
  const frontRef = useRef(null);
const backRef = useRef(null);

  const [isLoading, setIsLoading] = useState(false);
  const [storageStatus, setStorageStatus] = useState(null);

  const [cardData, setCardData] = useState({
    snfNo: "N/A",
    cardHolderName: "Not Available",
    mobile: "Not Available",
    address: "Not Available",
    familyDetails: []
  });

  const [flipped, setFlipped] = useState(false);
  const downloadPDF = async () => {
    const cardElement = cardRef.current;
    const originalFlipClass = cardElement.className;
  
    // Temporarily remove flip
    cardElement.classList.remove("flip");
  
    // Remove rotation from back
    const back = backRef.current;
    const originalTransform = back.style.transform;
    back.style.transform = "none";
  
    await new Promise(resolve => setTimeout(resolve, 200)); // Allow DOM update
  
    const pdf = new jsPDF("p", "mm", "a4");
  
    const captureAndAdd = async (ref, addNewPage = false) => {
      const canvas = await html2canvas(ref, { scale: 2 }); // High-res
      const imgData = canvas.toDataURL("image/png");
      const imgProps = pdf.getImageProperties(imgData);
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
  
      if (addNewPage) pdf.addPage();
  
      pdf.addImage(imgData, "PNG", 0, 0, pdfWidth, pdfHeight);
    };
  
    // Capture front side on page 1
    if (frontRef.current) await captureAndAdd(frontRef.current);
  
    // Capture back side on page 2
    if (backRef.current) await captureAndAdd(backRef.current, true);
  
    pdf.save("HealthCard.pdf");
  
    // Restore original state
    back.style.transform = originalTransform;
    cardElement.className = originalFlipClass;
  };
  
  


  // Calculate dates
  const issueDate = new Date();
  const expiryDate = new Date();
  expiryDate.setFullYear(expiryDate.getFullYear() + 1);

  const formatDate = (date) => date.toISOString().split("T")[0];

  // Generate barcode value (SNF + last 4 digits of mobile)
  const generateBarcodeValue = (data) => {
    const mobileDigits = data.mobile.slice(-4).padStart(4, '0');
    return `${data.snfNo}-${mobileDigits}`;
  };

  // Initialize card data
  useEffect(() => {
    const formData = location.state?.formData || 
                    JSON.parse(localStorage.getItem("healthCardFormData"));
    
    if (formData) {
      const completeCardData = {
        ...formData,
        issueDate: formatDate(issueDate),
        expiryDate: formatDate(expiryDate),
        barcode: generateBarcodeValue(formData)
      };
      setCardData(completeCardData);
      storeCardData(completeCardData);
    }
  }, [location.state]);

  const storeCardData = async (cardData) => {
    setIsLoading(true);
    setStorageStatus(null);
    
    try {
      const response = await fetch("http://localhost:5000/store-card", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ cardData }),
      });

      const data = await response.json();
      if (data.success) {
        setStorageStatus("success");
        localStorage.removeItem("healthCardFormData");
      } else {
        setStorageStatus("error");
        console.error("Failed to store health card:", data.message);
      }
    } catch (error) {
      setStorageStatus("error");
      console.error("Error storing health card:", error);
      // Fallback to localStorage if API fails
      localStorage.setItem("healthCardFormData", JSON.stringify(cardData));
    } finally {
      setIsLoading(false);
    }
  };

  if (!cardData) {
    return (
      <div className="card-wrapper">
        <h2>Error: No card data found</h2>
        <p>Please go back and complete the form again.</p>
        <button className="home-btn" onClick={() => navigate("/")}>
          üè† Go to Home
        </button>
      </div>
    );
  }

  return (
    <div className="card-wrapper">
      <h2 className="title">üéâ Happy Doctors Consultancy Card üéâ</h2>
      
      {isLoading && <div className="loading">Saving your card data...</div>}
      {storageStatus === "success" && (
        <div className="success-message">
          ‚úÖ Card data saved successfully!
        </div>
      )}
      {storageStatus === "error" && (
        <div className="error-message">
          ‚ùå Failed to save card data. Please contact support.
        </div>
      )}

<div
ref={cardRef}
  className={`card-container ${flipped ? "flip" : ""}`}
  onClick={() => setFlipped(!flipped)}
>

        {/* Front Side of the Card */}
        <div className="card front" ref={frontRef}>
          <div className="card-header">
            <img src={logo} alt="Company Logo" className="card-logo" />  {/* Logo Added */}
          </div>
          <div className="card-content">
            <p><strong>Card Holder:</strong> {cardData.cardHolderName}</p>
            <p><strong> Card No: </strong>{cardData.snfNo}</p>
            <p><strong>Phone:</strong> {cardData.mobile}</p>
            <p><strong>Address:</strong> {cardData.address}</p>
            <p><strong>Issued Date:</strong> {cardData.issueDate}</p>
            <p><strong>Valid Until:</strong> {cardData.expiryDate}</p>
            
            {/* Barcode */}
            <div className="barcode-container">
              <Barcode
                value={cardData.barcode || generateBarcodeValue(cardData)}
                format="CODE128"
                width={1.5}
                height={50}
                displayValue={true}
              />
            </div>
          </div>
        </div>
        

        {/* Back Side of the Card */}
        <div className="card back" ref={backRef}>
          <div className="card-header" >
            <img src={logo} alt="Company Logo" className="card-logo" /> {/* Logo above Family Details */}
            
          </div>
          <div className="card-content">
            <table className="family-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Relation</th>
                </tr>
              </thead>
              <tbody>
                {cardData.familyDetails.map((member, index) => (
                  <tr key={index}>
                    <td>{member.name}</td>
                    <td>{member.age}</td>
                    <td>{member.relation}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="card-footer">
            <p>Emergency Contact: {cardData.mobile}</p>
          </div>
        </div>
      </div>

      <div className="action-buttons">
  <button className="print-btn" onClick={downloadPDF}>
    üì• Download PDF
  </button>
  <button className="print-btn" onClick={() => window.print()}>
    üñ®Ô∏è Print Card
  </button>
  <button className="home-btn" onClick={() => navigate("/")}>
    üè† Go to Home
  </button>
</div>
</div>
  );
};

export default GenerateCard;
