import React, { useState } from "react";
import { auth, provider, signInWithPopup, signOut } from "../firebase";
import { useNavigate } from "react-router-dom";
import lgs from "../assets/lgs.jpg";

const GoogleLogin = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`Welcome ${result.user.displayName}`);
    } catch (error) {
      console.error("Google Sign-In Error:", error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      alert("Logged out successfully!");
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  return (
    <div style={styles.mainContainer}>
      {/* Header Section - Logo, Title, Sign-In */}
      <div style={styles.header}>
        <img src={lgs} alt="Logo" style={styles.logo} />
        <p style={styles.title}>Happy Doctors Consultancy</p>
        {!user && (
          <button onClick={signInWithGoogle} style={styles.button}>
            Sign in with Google
          </button>
        )}
      </div>

      {/* User Profile Section */}
      {user && (
        <div style={styles.profileContainer}>
          <div style={styles.profile}>
            <img src={user.photoURL} alt="Profile" style={styles.avatar} />
            <h3>{user.displayName}</h3>
            <p>{user.email}</p>
            <div style={styles.buttonGroup}>
              <button onClick={() => navigate("/healthcard")} style={styles.cardButton}>
                Happy Doctors Card
              </button>
              <button onClick={handleLogout} style={styles.button}>Logout</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// CSS Styles
const styles = {
  mainContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    width: "100%",
    padding: "20px",
  },

  header: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    width: "100%",
    maxWidth: "100%",
    padding: "10px 30px ",
    backgroundColor: "#f8f9fa",
    borderRadius: "10px",
    boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
   // Ensures responsiveness
  },

  logo: {
    width: "80px",
    height: "auto",
  },

  title: {
    fontSize: "35px",
    fontWeight: "bold",
    flexGrow: 1,
    textAlign: "center",
  
  },

  button: {
    backgroundColor: "#4285F4",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    whiteSpace: "nowrap",
  },

  profileContainer: {
    width: "100%",
    textAlign: "center",
    marginTop: "20px",
  },

  profile: {
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    display: "inline-block",
    maxWidth: "90%",
  },

  avatar: {
    width: "80px",
    borderRadius: "50%",
  },

  buttonGroup: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginTop: "10px",
  },

  cardButton: {
    backgroundColor: "green",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },

  "@media (max-width: 768px)": {
    header: {
      flexDirection: "column",
      alignItems: "center",
      textAlign: "center",
    },

    title: {
      fontSize: "18px",
    },

    button: {
      marginTop: "10px",
      fontSize: "14px",
      padding: "8px 16px",
    },
  },
};

export default GoogleLogin;
