import React, { useState } from "react";
import "./HealthCard.css";


const HealthCard = () => {
  const [formData, setFormData] = useState({
    snfNo: Math.floor(1000 + Math.random() * 9000), // 4-digit random number
    date: new Date().toISOString().split("T")[0], // Today's date
    cardHolderName: "",
    address: "",
    mobile: "",
    familyDetails: [""] // Array for family members
  });

  const handleChange = (e, index = null) => {
    const { name, value } = e.target;
    if (name === "familyDetails") {
      let newFamily = [...formData.familyDetails];
      newFamily[index] = value;
      setFormData({ ...formData, familyDetails: newFamily });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const addFamilyMember = () => {
    setFormData({ ...formData, familyDetails: [...formData.familyDetails, ""] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Data:", formData);
    alert("Health Card Generated Successfully!");
  };

  return (
    <div className="health-card">
      <h2>Happy Doctors Consultancy</h2>
      <div className="company-info">
  <p>Happy Doctors Consultancy</p>
  <p>14-//, 60 Feet Road, Near Bolla Brahmanaidu House, Narasaraopet, Palnadu Dist, A.P</p>
</div>

      <p>Fill in the details below to generate your health card.</p>

      <form onSubmit={handleSubmit}>
        <div>
          <label>SNF No:</label>
          <input type="text" value={formData.snfNo} readOnly />
        </div>

        <div>
          <label>Date:</label>
          <input type="text" value={formData.date} readOnly />
        </div>

        <div>
          <label>Card Holder Name:</label>
          <input type="text" name="cardHolderName" value={formData.cardHolderName} onChange={handleChange} required />
        </div>

        <div>
          <label>Address:</label>
          <input type="text" name="address" value={formData.address} onChange={handleChange} required />
        </div>

        <div>
          <label>Mobile No:</label>
          <input type="text" name="mobile" value={formData.mobile} onChange={handleChange} required />
        </div>

        <h3>Family Details:</h3>
        {formData.familyDetails.map((member, index) => (
          <div key={index}>
            <input type="text" name="familyDetails" value={member} onChange={(e) => handleChange(e, index)} required />
          </div>
        ))}
        <button type="button" onClick={addFamilyMember}>+ Add Family Member</button>

        <button type="submit">Generate Health Card</button>
      </form>
    </div>
  );
};

export default HealthCard;
