import React, { useState } from "react";
import { auth, provider, signInWithPopup, signOut } from "../firebase";
import { useNavigate } from "react-router-dom";
import lgs from "../assets/lgs.jpg";

const GoogleLogin = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`Welcome ${result.user.displayName}`);
    } catch (error) {
      console.error("Google Sign-In Error:", error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      alert("Logged out successfully!");
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  return (
    <div style={styles.mainContainer}>
      {/* Left Side - Logo */}
      <div style={styles.left}>
        <img src={lgs} alt="Logo" style={styles.logo} />
      </div>

      {/* Center - Expanded Happy Doctors Consultancy */}
      <div style={styles.center}>
        <p style={styles.title}>Happy Doctors Consultancy</p>
      </div>

      {/* Right Side - Google Sign-in Button */}
      <div style={styles.right}>
        {!user && (
          <button onClick={signInWithGoogle} style={styles.button}>
            Sign in with Google
          </button>
        )}
      </div>
    </div>
  );
};

const styles = {
  mainContainer: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "20px",
    textAlign: "center",
  },
  left: {
    flex: "1",
    textAlign: "left",
    paddingLeft: "50px",
  },
  center: {
    flex: "2",
    textAlign: "center",
  },
  right: {
    flex: "1",
    textAlign: "right",
    paddingRight: "50px",
  },
  logo: {
    width: "120px",
    height: "auto",
  },
  title: {
    fontSize: "28px", // Increased font size
    fontWeight: "bold",
    textTransform: "uppercase",
    letterSpacing: "1px",
    whiteSpace: "nowrap", // Ensures it stays in one line
  },
  button: {
    backgroundColor: "#4285F4",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default GoogleLogin;
