import React, { useState, useEffect } from "react";
import { auth, provider, signInWithPopup, signOut } from "../firebase";
import { useNavigate } from "react-router-dom";
import lgs from "../assets/lgs.jpg";

// Multilingual content
const translations = {
  en: {
    title: "Happy Doctors Consultancy",
    networkHospitals: "Network Hospitals",
    signIn: "Sign in with Google",
    logout: "Logout",
    welcome: "Welcome",
    loggedOut: "Logged out successfully!",
    backToRegions: "← Back to regions",
    hospitalsCount: "Hospitals",
    happyDoctorsCard: "Happy Doctors Card",
    selectLanguage: "Select Language",
    services: "Our Services",
    healthCard: "Health Card",
    snfNumber: "S.N.F. No.",
    contact: "Contact",
    service1: "Family Health Card services available 24/7 nationwide with special discounts on medical products",
    service2: "Tax benefits with Family Health Card (Rs. 500) and special distribution services",
    serviceTable: {
      service: "Service",
      regularPrice: "Regular Price",
      discountPrice: "Discounted Price"
    }
  },
  te: {
    title: "హ్యాపీ డాక్టర్స్ కన్సల్టెన్సీ",
    networkHospitals: "నెట్‌వర్క్ హాస్పిటల్స్",
    signIn: "Googleతో సైన్ ఇన్ చేయండి",
    logout: "లాగ్ అవుట్",
    welcome: "స్వాగతం",
    loggedOut: "విజయవంతంగా లాగ్ అవుట్ అయ్యారు!",
    backToRegions: "← ప్రాంతాలకు తిరిగి వెళ్లండి",
    hospitalsCount: "ఆసుపత్రులు",
    happyDoctorsCard: "హ్యాపీ డాక్టర్స్ కార్డ్",
    selectLanguage: "భాషను ఎంచుకోండి",
    services: "మా సేవలు",
    healthCard: "ఆరోగ్య కార్డ్",
    snfNumber: "S.N.F. నం.",
    contact: "సంప్రదించండి",
    service1: "ఫ్యామిలీ హెల్త్ కార్డ్ సేవలు 24/7 జాతీయంగా లభిస్తాయి, వైద్య ఉత్పత్తులపై ప్రత్యేక రాయితీలతో",
    service2: "ఫ్యామిలీ హెల్త్ కార్డ్ (రూ. 500)తో పన్ను ప్రయోజనాలు మరియు ప్రత్యేక పంపిణీ సేవలు",
    serviceTable: {
      service: "సేవ",
      regularPrice: "సాధారణ ధర",
      discountPrice: "తగ్గింపు ధర"
    }
  },
  hi: {
    title: "हैप्पी डॉक्टर्स कंसल्टेंसी",
    networkHospitals: "नेटवर्क अस्पताल",
    signIn: "Google के साथ साइन इन करें",
    logout: "लॉग आउट",
    welcome: "स्वागत है",
    loggedOut: "सफलतापूर्वक लॉग आउट कर दिया गया!",
    backToRegions: "← क्षेत्रों पर वापस जाएं",
    hospitalsCount: "अस्पताल",
    happyDoctorsCard: "हैप्पी डॉक्टर्स कार्ड",
    selectLanguage: "भाषा चुनें",
    services: "हमारी सेवाएं",
    healthCard: "स्वास्थ्य कार्ड",
    snfNumber: "S.N.F. नं.",
    contact: "संपर्क करें",
    service1: "फैमिली हेल्थ कार्ड सेवाएं 24/7 राष्ट्रव्यापी उपलब्ध हैं, चिकित्सा उत्पादों पर विशेष छूट के साथ",
    service2: "फैमिली हेल्थ कार्ड (रु. 500) के साथ कर लाभ और विशेष वितरण सेवाएं",
    serviceTable: {
      service: "सेवा",
      regularPrice: "नियमित मूल्य",
      discountPrice: "छूट मूल्य"
    }
  }
};

// Hospital data by region
const hospitalData = {
  "Narasaraopet": [
    "Narasaraopet General Hospital",
    "Sai Super Specialty Hospital",
    "Kamineni Hospitals Narasaraopet",
    "Guntur Medical College Branch"
  ],
  "Chilakaluripet": [
    "Chilakaluripet Community Hospital",
    "Life Care Hospital",
    "Sai Priya Hospital",
    "Palnadu Multi-Specialty"
  ],
  "Sathenapalli": [
    "Sathenapalli Government Hospital",
    "Sri Sai Hospital",
    "Vinayaka Hospital",
    "Ramesh Hospitals Branch"
  ],
  "Vinukonda": [
    "Vinukonda General Hospital",
    "NRI Medical College",
    "Palnadu Super Specialty Hospital",
    "Apollo Clinic"
  ],
  "Piduguralla": [
    "Piduguralla Community Hospital",
    "Sai Krishna Hospital",
    "Guntur General Hospital Branch",
    "Kamineni Primary Care"
  ]
};

// Service data
const serviceData = [
  { name: { en: "Medicine (Multispecialty)", te: "మందులు (మల్టీస్పెషాలిటీ)", hi: "दवाई (मल्टीस्पेशलिटी)" }, regularPrice: "5000-00", discountPrice: "4000-00" },
  { name: { en: "Blood Test", te: "రక్త పరీక్ష", hi: "रक्त परीक्षण" }, regularPrice: "1000-00", discountPrice: "800-00" },
  { name: { en: "Scanning", te: "స్కానింగ్", hi: "स्कैनिंग" }, regularPrice: "1000-00", discountPrice: "800-00" },
  { name: { en: "M.R.I. Scanning", te: "ఎమ్.ఆర్.ఐ. స్కానింగ్", hi: "एम.आर.आई. स्कैनिंग" }, regularPrice: "6000-00", discountPrice: "4800-00" },
  { name: { en: "Ultrasound", te: "అల్ట్రాసౌండ్", hi: "अल्ट्रासाउंड" }, regularPrice: "2400-00", discountPrice: "1800-00" },
  { name: { en: "X-Ray", te: "ఎక్స్-రే", hi: "एक्स-रे" }, regularPrice: "3500-00", discountPrice: "2800-00" },
  { name: { en: "I.C.U (1 day)", te: "ఐ.సి.యూ (1 రోజు)", hi: "आई.सी.यू (1 दिन)" }, regularPrice: "3500-00", discountPrice: "2800-00" },
  { name: { en: "General Ward (1 day)", te: "సాధారణ వార్డ్ (1 రోజు)", hi: "सामान्य वार्ड (1 दिन)" }, regularPrice: "2000-00", discountPrice: "1600-00" }
];

const GoogleLogin = () => {
  const [user, setUser] = useState(null);
  const [showRegions, setShowRegions] = useState(false);
  const [selectedRegion, setSelectedRegion] = useState(null);
  const [isMobile, setIsMobile] = useState(false);
  const [language, setLanguage] = useState("en");
  const [showLanguageDropdown, setShowLanguageDropdown] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`${translations[language].welcome} ${result.user.displayName}`);
    } catch (error) {
      console.error("Google Sign-In Error:", error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      setSelectedRegion(null);
      alert(translations[language].loggedOut);
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  const toggleRegions = (e) => {
    e.stopPropagation();
    setShowRegions(!showRegions);
    if (!showRegions) {
      setSelectedRegion(null);
    }
  };

  const toggleLanguageDropdown = (e) => {
    e.stopPropagation();
    setShowLanguageDropdown(!showLanguageDropdown);
  };

  const selectRegion = (region) => {
    setSelectedRegion(region);
    setShowRegions(false);
  };

  const selectLanguage = (lang) => {
    setLanguage(lang);
    setShowLanguageDropdown(false);
  };

  const closeDropdowns = () => {
    setShowRegions(false);
    setShowLanguageDropdown(false);
  };

  useEffect(() => {
    document.addEventListener('click', closeDropdowns);
    return () => document.removeEventListener('click', closeDropdowns);
  }, []);

  const t = translations[language];

  return (
    <div style={styles.mainContainer}>
      <div style={styles.header}>
        <div style={styles.logoTitleContainer}>
          <img src={lgs} alt="Logo" style={styles.logo} />
          <h1 style={styles.title}>{t.title}</h1>
        </div>

        <div style={styles.buttonsContainer}>
          <div style={styles.languageDropdownContainer}>
            <button 
              onClick={toggleLanguageDropdown}
              style={{
                ...styles.languageButton,
                width: isMobile ? '100%' : 'auto'
              }}
            >
              {t.selectLanguage} {showLanguageDropdown ? '▲' : '▼'}
            </button>
            {showLanguageDropdown && (
              <div style={{
                ...styles.languageOptions,
                width: isMobile ? '100%' : 'auto'
              }}>
                <div 
                  style={styles.languageOption}
                  onClick={() => selectLanguage('en')}
                >
                  English
                </div>
                <div 
                  style={styles.languageOption}
                  onClick={() => selectLanguage('te')}
                >
                  తెలుగు
                </div>
                <div 
                  style={styles.languageOption}
                  onClick={() => selectLanguage('hi')}
                >
                  हिन्दी
                </div>
              </div>
            )}
          </div>

          <div style={styles.dropdownContainer}>
            <button 
              onClick={toggleRegions}
              style={{
                ...styles.networkButton,
                width: isMobile ? '100%' : 'auto'
              }}
            >
              {t.networkHospitals} {showRegions ? '▲' : '▼'}
            </button>
            {(showRegions || selectedRegion) && (
              <div style={{
                ...styles.regionsDropdown,
                width: isMobile ? '100%' : 'auto'
              }}>
                {!selectedRegion ? (
                  Object.keys(hospitalData).map((region) => (
                    <div 
                      key={region}
                      style={styles.regionItem}
                      onClick={() => selectRegion(region)}
                    >
                      {region}
                    </div>
                  ))
                ) : (
                  <div style={styles.hospitalListContainer}>
                    <div 
                      style={styles.backButton}
                      onClick={() => setSelectedRegion(null)}
                    >
                      {t.backToRegions}
                    </div>
                    <h4 style={styles.regionTitle}>
                      {selectedRegion} {t.hospitalsCount} ({hospitalData[selectedRegion].length})
                    </h4>
                    {hospitalData[selectedRegion].map((hospital, index) => (
                      <div key={index} style={styles.hospitalItem}>
                        • {hospital}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
          
          {!user ? (
            <button 
              onClick={signInWithGoogle} 
              style={{
                ...styles.button,
                width: isMobile ? '100%' : 'auto'
              }}
            >
              {t.signIn}
            </button>
          ) : (
            <button 
              onClick={handleLogout} 
              style={{
                ...styles.button,
                width: isMobile ? '100%' : 'auto'
              }}
            >
              {t.logout}
            </button>
          )}
        </div>
      </div>

      {/* Profile section - shown only after sign in */}
      {user && (
        <div style={styles.profileContainer}>
          <div style={styles.profile}>
            <img 
              src={user.photoURL || "https://via.placeholder.com/100"} 
              alt="Profile" 
              style={styles.avatar} 
            />
            <h3 style={styles.userName}>{user.displayName}</h3>
            <p style={styles.userEmail}>{user.email}</p>
            <button 
              onClick={() => navigate("/healthcard")} 
              style={styles.cardButton}
            >
              {t.happyDoctorsCard}
            </button>
          </div>
        </div>
      )}
{!user && (
  <div style={styles.healthCardContainer}>
    <div style={styles.cardHeader}>
      <div style={styles.cardTitleContainer}>
        <h2 style={styles.cardTitle}>{t.healthCard}</h2>
      </div>
      <p style={styles.contact}>{t.contact}: 88 976 976 98, 88 976 976 98</p>
    </div>

    <div style={styles.servicesSection}>
      <h3 style={styles.sectionTitle}>{t.services}</h3>
      <div style={styles.serviceItem}>
        <p style={styles.serviceText}>{t.service1}</p>
      </div>
      <div style={styles.serviceItem}>
        <p style={styles.serviceText}>{t.service2}</p>
      </div>
    </div>

    <div style={styles.servicesTable}>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.th}>{t.serviceTable.service}</th>
            <th style={styles.th}>{t.serviceTable.regularPrice}</th>
            <th style={styles.th}>{t.serviceTable.discountPrice}</th>
          </tr>
        </thead>
        <tbody>
          {serviceData.map((service, index) => (
            <tr key={index} style={index % 2 === 0 ? styles.evenRow : styles.oddRow}>
              <td style={styles.td}>{service.name[language]}</td>
              <td style={styles.td}>{service.regularPrice}</td>
              <td style={styles.td}>{service.discountPrice}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  </div>
)}
{!user && (
  <div className="services-section">
    {/* 🌟 Our Services - Already visible before login */}
    <div className="service-row">
      {/* Existing "Our Services" cards go here */}
    </div>

