import React, { useState } from "react";
import { auth, provider, signInWithPopup, signOut } from "../firebase";
import { useNavigate } from "react-router-dom";
import lgs from "../assets/lgs.jpg";

const GoogleLogin = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`Welcome ${result.user.displayName}`);
    } catch (error) {
      console.error("Google Sign-In Error:", error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      alert("Logged out successfully!");
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  return (
    <div style={styles.mainContainer}>
      {/* Left Side - Logo and Title */}
      <div style={styles.left}>
        <img src={lgs} alt="Logo" style={styles.logo} />
        <p style={styles.title}>Happy Doctors Consultancy</p>
      </div>

      {/* Right Side - Google Sign-in Button */}
      <div style={styles.right}>
        {!user && (
          <button onClick={signInWithGoogle} style={styles.button}>
            Sign in with Google
          </button>
        )}
      </div>

      {/* Center Content - User Profile */}
      {user && (
        <div style={styles.center}>
          <div style={styles.profile}>
            <img src={user.photoURL} alt="Profile" style={styles.avatar} />
            <h3>{user.displayName}</h3>
            <p>{user.email}</p>
            <div style={styles.buttonGroup}>
              <button onClick={() => navigate("/healthcard")} style={styles.cardButton}>
                Happy Doctors Card
              </button>
              <button onClick={handleLogout} style={styles.button}>Logout</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// CSS Styles
const styles = {
  mainContainer: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "20px",
    textAlign: "center",
    flexWrap: "wrap", // ✅ Ensures responsiveness
  },

  left: {
    display: "flex",
    alignItems: "center",
    gap: "10px", // ✅ Adjust spacing
    flex: 1,
  },

  title: {
    fontSize: "29px",
    fontWeight: "bold",
    whiteSpace: "nowrap", // ✅ Prevents breaking
  },

  right: {
    display: "flex",
    alignItems: "center",
  },

  logo: {
    width: "100px",
    height: "auto",
    maxWidth: "20vw", // ✅ Dynamic scaling
  },

  center: {
    width: "100%",
    textAlign: "center",
    marginTop: "20px",
  },

  profile: {
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    display: "inline-block",
    maxWidth: "90%",
  },

  avatar: {
    width: "80px",
    borderRadius: "50%",
  },

  buttonGroup: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginTop: "10px",
  },

  button: {
    backgroundColor: "#4285F4",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    whiteSpace: "nowrap",
  },

  cardButton: {
    backgroundColor: "green",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },

  // ✅ Mobile Styles
  "@media (max-width: 768px)": {
    mainContainer: {
      flexDirection: "column", // ✅ Stack elements
      alignItems: "center",
    },

    left: {
      flexDirection: "column",
      alignItems: "center",
      textAlign: "center",
    },

    title: {
      fontSize: "6vw", // ✅ Adjust font size
    },

    right: {
      textAlign: "center",
      marginTop: "10px",
    },

    button: {
      fontSize: "14px",
      padding: "8px 16px",
    },
  },
};

export default GoogleLogin;
