import React, { useState } from "react";
import { auth, provider, signInWithPopup, signOut } from "../firebase";
import { useNavigate } from "react-router-dom";
import lgs from "../assets/lgs.jpg";

const GoogleLogin = () => {
  const [user, setUser] = useState(null);
  const [showHospitals, setShowHospitals] = useState(false);
  const navigate = useNavigate();

  const hospitals = [
    "Guntur General Hospital",
    "Palnadu Super Specialty Hospital",
    "NRI Medical College",
    "Kamineni Hospitals",
    "Sai Priya Hospital",
    "Sri Sai Hospital",
    "Life Care Hospital"
  ];

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`Welcome ${result.user.displayName}`);
    } catch (error) {
      console.error("Google Sign-In Error:", error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      alert("Logged out successfully!");
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  const toggleHospitals = () => {
    setShowHospitals(!showHospitals);
  };

  return (
    <div style={styles.mainContainer}>
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <img src={lgs} alt="Logo" style={styles.logo} />
          <p style={styles.title}>Happy Doctors Consultancy</p>
        </div>

        <div style={styles.buttonsContainer}>
          <div style={styles.dropdownContainer}>
            <button onClick={toggleHospitals} style={styles.hospitalsButton}>
              Hospitals in Palnadu
            </button>
            {showHospitals && (
              <div style={styles.dropdownContent}>
                {hospitals.map((hospital, index) => (
                  <div key={index} style={styles.hospitalItem}>{hospital}</div>
                ))}
              </div>
            )}
          </div>
          
          {!user && (
            <button onClick={signInWithGoogle} style={styles.button}>
              Sign in with Google
            </button>
          )}
        </div>
      </div>

      {user && (
        <div style={styles.profileContainer}>
          <div style={styles.profile}>
            <img src={user.photoURL} alt="Profile" style={styles.avatar} />
            <h3>{user.displayName}</h3>
            <p>{user.email}</p>
            <div style={styles.buttonGroup}>
              <button onClick={() => navigate("/healthcard")} style={styles.cardButton}>
                Happy Doctors Card
              </button>
              <button onClick={handleLogout} style={styles.button}>Logout</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  mainContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    width: "100%",
    padding: "0px",
    boxSizing: "border-box",
     
  },

  header: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    width: "100%",
    padding: "10px 20px",
    backgroundColor: "#f8f9fa",
    borderRadius: "10px",
    boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
    boxSizing: "border-box",
    flexWrap: "wrap",
    gap: "35px",
    backgroundColor:"lightgreen",
  },

  headerContent: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    minWidth: "200px",
    flex: "1 1 auto",
  },

  buttonsContainer: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    flexWrap: "wrap",
    justifyContent: "flex-end",
    flex: "1 1 auto",
  },

  logo: {
    width: "100px",
    height: "auto",
    flexShrink: 0,
    borderRadius:"75px",
  },

  title: {
    fontSize: "clamp(18px, 2.5vw, 35px)",
    fontWeight: "bold",
    whiteSpace: "nowrap",
    overflow: "hidden",
    textOverflow: "ellipsis",
    margin: 0,
  },

  dropdownContainer: {
    position: "relative",
    display: "inline-block",
  },

  hospitalsButton: {
    backgroundColor: "#6c757d",
    color: "white",
    padding: "10px 15px",
    fontSize: "clamp(12px, 1.5vw, 16px)",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    whiteSpace: "nowrap",
  },

  dropdownContent: {
    position: "absolute",
    backgroundColor: "#f9f9f9",
    minWidth: "200px",
    boxShadow: "0px 8px 16px 0px rgba(0,0,0,0.2)",
    zIndex: 1,
    borderRadius: "5px",
    maxHeight: "300px",
    overflowY: "auto",
    right: 0,
  },

  hospitalItem: {
    color: "black",
    padding: "12px 16px",
    textDecoration: "none",
    display: "block",
    borderBottom: "1px solid #ddd",
    "&:hover": {
      backgroundColor: "#f1f1f1"
    }
  },

  button: {
    backgroundColor: "#4285F4",
    color: "white",
    padding: "10px 15px",
    fontSize: "clamp(12px, 1.5vw, 16px)",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    whiteSpace: "nowrap",
  },

  profileContainer: {
    width: "100%",
    textAlign: "center",
    marginTop: "20px",
  },

  profile: {
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    display: "inline-block",
    maxWidth: "90%",
    boxSizing: "border-box",
  },

  avatar: {
    width: "80px",
    borderRadius: "50%",
  },

  buttonGroup: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginTop: "10px",
  },

  cardButton: {
    backgroundColor: "green",
    color: "white",
    padding: "10px 15px",
    fontSize: "clamp(12px, 1.5vw, 16px)",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },

  "@media (max-width: 600px)": {
    header: {
      flexDirection: "column",
      alignItems: "center",
      gap: "10px",
      padding: "15px",
    },

    headerContent: {
      width: "100%",
      justifyContent: "center",
    },

    buttonsContainer: {
      width: "100%",
      justifyContent: "center",
    },

    title: {
      whiteSpace: "normal",
      textAlign: "center",
    },

    dropdownContent: {
      width: "100%",
    }
  },

  "@media (max-width: 400px)": {
    logo: {
      width: "50px",
    },
    
    title: {
      fontSize: "18px",
    },
    
    hospitalsButton: {
      width: "100%",
    },
    
    button: {
      width: "100%",
    }
  }
};

export default GoogleLogin;
