import React, { useState } from "react";
import { auth, provider, signInWithPopup, signOut } from "../firebase";
import { useNavigate } from "react-router-dom";
import lgs from "../assets/lgs.jpg";

const GoogleLogin = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`Welcome ${result.user.displayName}`);
    } catch (error) {
      console.error("Google Sign-In Error:", error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      alert("Logged out successfully!");
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  return (
    <div style={styles.mainContainer}>
      {/* Left Side - Logo and Title */}
      <div style={styles.left}>
        <img src={lgs} alt="Logo" style={styles.logo} />
        <p style={styles.title}>Happy Doctors Consultancy</p>
      </div>

      {/* Center Content */}
      <div style={styles.center}>
        {user ? (
          <div style={styles.profile}>
            <img src={user.photoURL} alt="Profile" style={styles.avatar} />
            <h3>{user.displayName}</h3>
            <p>{user.email}</p>
            <div style={styles.buttonGroup}>
              <button onClick={() => navigate("/healthcard")} style={styles.cardButton}>
                Happy Doctors Card
              </button>
              <button onClick={handleLogout} style={styles.button}>Logout</button>
            </div>
          </div>
        ) : null}
      </div>

      {/* Right Side - Google Sign-in Button */}
      <div style={styles.right}>
        {!user && (
          <button onClick={signInWithGoogle} style={styles.button}>
            Sign in with Google
          </button>
        )}
      </div>
    </div>
  );
};

const styles = {
  mainContainer: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "20px",
    textAlign: "center",
  },
  left: {
    flex: "1",
    textAlign: "left",
    paddingLeft: "50px",
    display: "flex",
    alignItems: "center",
    gap: "50px", // Space between logo and text
  },
  title: {
    fontSize: "24px", // Adjust size to match logo height
    fontWeight: "bold",
  },
  center: { flex: "2", textAlign: "center" },
  right: { flex: "1", textAlign: "right", paddingRight: "50px" },
  logo: { width: "120px", height: "auto" },
  profile: {
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    display: "inline-block",
  },
  avatar: { width: "100px", borderRadius: "50%" },
  buttonGroup: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginTop: "10px",
  },
  button: {
    backgroundColor: "#4285F4",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  cardButton: {
    backgroundColor: "green",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default GoogleLogin;
