import React, { useState } from "react";
import { auth, provider, signInWithPopup, signOut } from "../firebase";
import { useNavigate } from "react-router-dom"; // âœ… Import useNavigate

const GoogleLogin = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate(); // âœ… Initialize navigation

  // ðŸ”¹ Handle Google Sign-In
  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`Welcome ${result.user.displayName}`);
    } catch (error) {
      if (error.code === "auth/cancelled-popup-request") {
        console.log("Popup closed before sign-in completed.");
      } else if (error.code === "auth/popup-blocked") {
        alert("Popup blocked! Please allow popups for this site.");
      } else {
        console.error("Google Sign-In Error:", error.message);
      }
    }
  };

  // ðŸ”¹ Handle Logout
  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      alert("Logged out successfully!");
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  // âœ… Redirect to Health Card Page
  const goToHealthCard = () => {
    if (!user) {
      alert("Please sign in first!");
      return;
    }
    navigate("/healthcard", {
      state: {
        cardData: {
          snfNo: "97/2024",
          phone: user.phoneNumber || "N/A",
          cardNo: "HC123456",
          date: new Date().toLocaleDateString(),
          cardHolderName: user.displayName,
          address: "60 Feet Road, Narasaraopet, Palnadu Dist, A.P",
          mobile: user.phoneNumber || "N/A",
          familyDetails: ["Alice Doe", "Bob Doe"], // Example family details
        },
      },
    });
  };

  return (
    <div style={styles.container}>
      {user ? (
        <div style={styles.profile}>
          <img src={user.photoURL} alt="Profile" style={styles.avatar} />
          <h3>{user.displayName}</h3>
          <p>{user.email}</p>

          {/* âœ… Happy Doctors Card Button */}
          <button onClick={goToHealthCard} style={styles.cardButton}>
            Happy Doctors Card
          </button>

          {/* Logout Button */}
          <button onClick={handleLogout} style={styles.button}>Logout</button>
        </div>
      ) : (
        <button onClick={signInWithGoogle} style={styles.button}>
          Sign in with Google
        </button>
      )}
    </div>
  );
};

// ðŸ”¹ Inline CSS for styling
const styles = {
  container: {
    textAlign: "center",
    marginTop: "50px",
  },
  button: {
    backgroundColor: "#4285F4",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
  },
  cardButton: {
    backgroundColor: "#34A853", // Green color for Happy Doctors Card
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
  },
  profile: {
    marginTop: "20px",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    display: "inline-block",
  },
  avatar: {
    width: "100px",
    borderRadius: "50%",
  },
};

export default GoogleLogin;
