import React, { useEffect, useState, useRef } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Barcode from "react-barcode";
import "./GenerateCard.css";
import logo from "../assets/logo.png";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";

const GenerateCard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const cardRef = useRef(null);

  const [isLoading, setIsLoading] = useState(false);
  const [storageStatus, setStorageStatus] = useState(null);
  const [cardData, setCardData] = useState({
    snfNo: "N/A",
    cardHolderName: "Not Available",
    mobile: "Not Available",
    address: "Not Available",
    familyDetails: []
  });

  const downloadPDF = async () => {
    const card = cardRef.current;

    const canvas = await html2canvas(card, {
      scale: window.devicePixelRatio || 2,
      useCORS: true
    });

    const imgData = canvas.toDataURL("image/png");

    const pdf = new jsPDF("l", "mm", "a4");

    const pdfWidth = pdf.internal.pageSize.getWidth();
    const pdfHeight = pdf.internal.pageSize.getHeight();

    const imgProps = pdf.getImageProperties(imgData);
    const imgRatio = imgProps.width / imgProps.height;

    let imgWidth = pdfWidth;
    let imgHeight = pdfWidth / imgRatio;

    if (imgHeight > pdfHeight) {
      imgHeight = pdfHeight;
      imgWidth = pdfHeight * imgRatio;
    }

    const x = (pdfWidth - imgWidth) / 2;
    const y = (pdfHeight - imgHeight) / 2;

    pdf.addImage(imgData, "PNG", x, y, imgWidth, imgHeight);
    pdf.save("HappyDoctorsCard.pdf");
  };

  const issueDate = new Date();
  const expiryDate = new Date();
  expiryDate.setFullYear(expiryDate.getFullYear() + 1);

  const formatDate = (date) => date.toISOString().split("T")[0];

  const generateBarcodeValue = (data) => {
    const mobileDigits = data.mobile.slice(-4).padStart(4, '0');
    return `${data.snfNo}-${mobileDigits}`;
  };

  useEffect(() => {
    const formData = location.state?.formData ||
      JSON.parse(localStorage.getItem("healthCardFormData"));

    if (formData) {
      const completeCardData = {
        ...formData,
        issueDate: formatDate(issueDate),
        expiryDate: formatDate(expiryDate),
        barcode: generateBarcodeValue(formData)
      };
      setCardData(completeCardData);
      storeCardData(completeCardData);
    }
  }, [location.state]);

  const storeCardData = async (cardData) => {
    setIsLoading(true);
    setStorageStatus(null);

    try {
      const response = await fetch("http://localhost:5000/store-card", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ cardData }),
      });

      const data = await response.json();
      if (data.success) {
        setStorageStatus("success");
        localStorage.removeItem("healthCardFormData");
      } else {
        setStorageStatus("error");
        console.error("Failed to store health card:", data.message);
      }
    } catch (error) {
      setStorageStatus("error");
      console.error("Error storing health card:", error);
      localStorage.setItem("healthCardFormData", JSON.stringify(cardData));
    } finally {
      setIsLoading(false);
    }
  };

  if (!cardData) {
    return (
      <div className="card-wrapper">
        <h2>Error: No card data found</h2>
        <p>Please go back and complete the form again.</p>
        <button className="home-btn" onClick={() => navigate("/")}>
          🏠 Go to Home
        </button>
      </div>
    );
  }

  return (
    <div className="card-wrapper">
      <h2 className="title">🎉 Happy Doctors Consultancy Card 🎉</h2>

      {isLoading && <div className="loading">Saving your card data...</div>}
      {storageStatus === "success" && (
        <div className="success-message">
          ✅ Card data saved successfully!
        </div>
      )}
      {storageStatus === "error" && (
        <div className="error-message">
          ❌ Failed to save card data. Please contact support.
        </div>
      )}

      <div ref={cardRef} className="card-container">

        {/* FRONT SIDE */}
        <div className="card">
          <div className="card-header">
            <img src={logo} alt="Company Logo" className="card-logo" />
          </div>
          <div className="card-content">
            <p><strong>Card Holder:</strong> {cardData.cardHolderName}</p>
            <p><strong>Card No:</strong> {cardData.snfNo}</p>
            <p><strong>Phone:</strong> {cardData.mobile}</p>
            <p><strong>Address:</strong> {cardData.address}</p>
            <p><strong>Issued Date:</strong> {cardData.issueDate}</p>
            <p><strong>Valid Until:</strong> {cardData.expiryDate}</p>
            <div className="barcode-container">
              <Barcode
                value={cardData.barcode}
                format="CODE128"
                width={1.5}
                height={50}
                displayValue={true}
              />
            </div>
          </div>
        </div>

        {/* BACK SIDE */}
        <div className="card">
          <div className="card-header">
            <img src={logo} alt="Company Logo" className="card-logo" />
          </div>
          <div className="card-content">
            <table className="family-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Relation</th>
                </tr>
              </thead>
              <tbody>
                {cardData.familyDetails.map((member, index) => (
                  <tr key={index}>
                    <td>{member.name}</td>
                    <td>{member.age}</td>
                    <td>{member.relation}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="card-footer">
            <p>Emergency Contact: {cardData.mobile}</p>
          </div>
        </div>
      </div>

      <div className="action-buttons">
        <button className="print-btn" onClick={downloadPDF}>📥 Download PDF</button>
        <button className="print-btn" onClick={() => window.print()}>🖨️ Print Card</button>
        <button className="home-btn" onClick={() => navigate("/")}>🏠 Go to Home</button>
      </div>
    </div>
  );
};

export default GenerateCard;
