import React, { useEffect, useState, useRef } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Barcode from "react-barcode";
import html2canvas from "html2canvas";
import "./GenerateCard.css";
import logo from "../assets/logo.png";
import { auth, database, ref, set, get } from "../firebase";

const GenerateCard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const frontRef = useRef(null);
  const backRef = useRef(null);
  const [downloading, setDownloading] = useState(false);

  const [cardData, setCardData] = useState({
    snfNo: "N/A",
    cardHolderName: "Not Available",
    mobile: "Not Available",
    address: "Not Available",
    familyDetails: []
  });

  const [flipped, setFlipped] = useState(false);

  // Calculate dates
  const issueDate = new Date();
  const expiryDate = new Date();
  expiryDate.setFullYear(expiryDate.getFullYear() + 1);

  const saveCardToFirebase = (data) => {
    const user = auth.currentUser;
    if (user) {
      const userRef = ref(database, `users/${user.uid}/card`);
      set(userRef, data)
        .then(() => {
          console.log("âœ… Card saved successfully to Firebase");
          navigate("/profile");
        })
        .catch((error) => {
          console.error("âŒ Error saving card:", error);
        });
    } else {
      console.warn("âš ï¸ No authenticated user found");
    }
  };

  const formatDate = (date) => date.toISOString().split("T")[0];

  const generateBarcodeValue = (data) => {
    const mobileDigits = data.mobile.slice(-4).padStart(4, '0');
    return `${data.snfNo}-${mobileDigits}`;
  };

  const fetchCardFromFirebase = async (cardId) => {
    try {
      const snapshot = await get(ref(database, `healthCards/${cardId}`));
      if (snapshot.exists()) {
        return snapshot.val();
      }
      return null;
    } catch (error) {
      console.error("Error fetching card:", error);
      return null;
    }
  };

  const downloadCard = async () => {
    setDownloading(true);
    try {
      // Show both sides temporarily
      setFlipped(false);
      
      // Wait for the front side to render
      await new Promise(resolve => setTimeout(resolve, 500));
      
      const frontCanvas = await html2canvas(frontRef.current, {
        scale: 2, // Higher quality
        logging: false,
        useCORS: true,
        allowTaint: true
      });
      
      // Flip to back side
      setFlipped(true);
      
      // Wait for the back side to render
      await new Promise(resolve => setTimeout(resolve, 500));
      
      const backCanvas = await html2canvas(backRef.current, {
        scale: 2, // Higher quality
        logging: false,
        useCORS: true,
        allowTaint: true
      });
      
      // Create a new canvas to combine both sides
      const combinedCanvas = document.createElement('canvas');
      const combinedWidth = Math.max(frontCanvas.width, backCanvas.width);
      const combinedHeight = frontCanvas.height + backCanvas.height + 20; // Add some spacing
      
      combinedCanvas.width = combinedWidth;
      combinedCanvas.height = combinedHeight;
      
      const ctx = combinedCanvas.getContext('2d');
      
      // Draw front side
      ctx.drawImage(frontCanvas, (combinedWidth - frontCanvas.width) / 2, 0);
      
      // Draw back side
      ctx.drawImage(backCanvas, (combinedWidth - backCanvas.width) / 2, frontCanvas.height + 20);
      
      // Add watermark or additional info if needed
      ctx.font = '16px Arial';
      ctx.fillStyle = '#666';
      ctx.textAlign = 'center';
      ctx.fillText('Happy Doctors Consultancy - Health Card', combinedWidth / 2, combinedHeight - 15);
      
      // Convert to image and download
      const link = document.createElement('a');
      link.download = `HealthCard_${cardData.cardHolderName}.png`;
      link.href = combinedCanvas.toDataURL('image/png');
      link.click();
      
    } catch (error) {
      console.error("Error downloading card:", error);
    } finally {
      setDownloading(false);
    }
  };

  useEffect(() => {
    const loadCardData = async () => {
      let formData = location.state?.formData ||
        JSON.parse(localStorage.getItem("healthCardFormData"));
      
      if (location.state?.cardId && !formData) {
        const firebaseData = await fetchCardFromFirebase(location.state.cardId);
        if (firebaseData) formData = firebaseData;
      }
      
      if (formData) {
        const completeCardData = {
          ...formData,
          issueDate: formatDate(issueDate),
          expiryDate: formatDate(expiryDate),
          barcode: generateBarcodeValue(formData)
        };
        setCardData(completeCardData);
        saveCardToFirebase(completeCardData);
      }
    };
    
    loadCardData();
  }, [location.state]);

  if (!cardData) {
    return (
      <div className="card-wrapper">
        <h2>Error: No card data found</h2>
        <p>Please go back and complete the form again.</p>
        <button className="profile-btn" onClick={() => navigate("/profile")}>
          ðŸ‘¤ Go to Profile
        </button>
      </div>
    );
  }

  return (
    <div className="card-wrapper">
      <h2 className="title">ðŸŽ‰ Happy Doctors Consultancy Card ðŸŽ‰</h2>

      <div className="card-controls">
        <button 
          className="flip-btn" 
          onClick={() => setFlipped(!flipped)}
        >
          {flipped ? "Show Front" : "Show Back"}
        </button>
        <button 
          className="download-btn" 
          onClick={downloadCard}
          disabled={downloading}
        >
          {downloading ? "Downloading..." : "ðŸ“¥ Download Card"}
        </button>
      </div>

      <div className={`card-container ${flipped ? "flip" : ""}`}>
        {/* Front Side */}
        <div className="card front" ref={frontRef}>
          <div className="card-header">
            <img src={logo} alt="Company Logo" className="card-logo" />
            <h3>Health Card - Front</h3>
          </div>
          <div className="card-content">
            <div className="card-field">
              <strong>Card Holder:</strong> {cardData.cardHolderName}
            </div>
            <div className="card-field">
              <strong>Card No:</strong> {cardData.snfNo}
            </div>
            <div className="card-field">
              <strong>Phone:</strong> {cardData.mobile}
            </div>
            <div className="card-field">
              <strong>Address:</strong> {cardData.address}
            </div>
            <div className="card-field">
              <strong>Issued Date:</strong> {cardData.issueDate}
            </div>
            <div className="card-field">
              <strong>Valid Until:</strong> {cardData.expiryDate}
            </div>
            
            <div className="barcode-container">
              <Barcode
                value={cardData.barcode || generateBarcodeValue(cardData)}
                format="CODE128"
                width={1.5}
                height={50}
                displayValue={true}
              />
            </div>
          </div>
        </div>
        
        {/* Back Side */}
        <div className="card back" ref={backRef}>
          <div className="card-header">
            <img src={logo} alt="Company Logo" className="card-logo" />
            <h3>Health Card - Back</h3>
          </div>
          <div className="card-content">
            <h4>Family Members</h4>
            <table className="family-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Relation</th>
                </tr>
              </thead>
              <tbody>
                {cardData.familyDetails.map((member, index) => (
                  <tr key={index}>
                    <td>{member.name}</td>
                    <td>{member.age}</td>
                    <td>{member.relation}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="card-footer">
            <p><strong>Emergency Contact:</strong> {cardData.mobile}</p>
            <p className="disclaimer">
              This card is property of Happy Doctors Consultancy. 
              Please present this card when availing services.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GenerateCard;
