import React, { useState } from "react";
import qrCodeImage from "../assets/qr.jpg"; // Your QR Code image
import "./Payment.css"; // Import CSS
import { useNavigate,useLocation } from "react-router-dom";
import { database, ref, get } from "../firebase";

const Payment = () => {
  const navigate=useNavigate();
  const location=useLocation();
  const [transactionId, setTransactionId] = useState("");
  const [paymentStatus, setPaymentStatus] = useState(null);


const handleVerifyPayment = async () => {
  if (!transactionId.trim()) {
    alert("Please enter a valid transaction ID.");
    return;
  }

  try {
    // Check if transaction exists in Firebase
    const transactionRef = ref(database, `transactions/${transactionId}`);
    const snapshot = await get(transactionRef);
    
    if (snapshot.exists()) {
      alert("❌ Payment Failed: Transaction ID already used!");
      return;
    }
    
    // Save the transaction
    await set(ref(database, `transactions/${transactionId}`), {
      timestamp: new Date().toISOString(),
      cardId: location.state?.cardId,
      amount: 500 // Or your payment amount
    });
    
    // Payment successful
    setPaymentStatus("success");
    alert("✅ Payment Successful!");
    
    // Navigate to generate card with form data
    const formData = location.state?.formData || 
      JSON.parse(localStorage.getItem("healthCardFormData"));
    
    navigate("/generate-card", { state: { formData } });
  } catch (error) {
    console.error("Error verifying payment:", error);
    alert("❌ Error verifying payment.");
  }
};

  return (
    <div className="payment-container">
      <h2>Scan & Pay</h2>
      <p>Scan the QR code below to complete the payment.</p>

      <img src={qrCodeImage} alt="UPI QR Code" className="qr-code" />

      <div className="input-section">
        <label>Enter Transaction ID:</label>
        <input
          type="text"
          placeholder="Enter UPI Transaction ID"
          value={transactionId}
          onChange={(e) => setTransactionId(e.target.value)}
        />
        <button onClick={handleVerifyPayment} className="verify-button">
          Verify Payment
        </button>
      </div>

      {paymentStatus === "success" && <p className="success-msg">✅ Payment Successful!</p>}
      {paymentStatus === "failed" && <p className="error-msg">❌ Payment Failed! Duplicate or Invalid Transaction.</p>}
    </div>
  );
};

export default Payment;
