import React, { useState } from "react";
import qrCodeImage from "../assets/qr.jpg";
import "./Payment.css";
import { useNavigate, useLocation } from "react-router-dom";
import { database, ref, query, orderByChild, equalTo, get } from "../firebase"; // Use your existing Firebase config

const Payment = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [transactionId, setTransactionId] = useState("");
  const [paymentStatus, setPaymentStatus] = useState(null);
  const [isVerifying, setIsVerifying] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleVerifyPayment = async () => {
    if (!transactionId.trim()) {
      setErrorMessage("Please enter a valid transaction ID.");
      return;
    }

    setIsVerifying(true);
    setErrorMessage("");

    try {
      // 1. Query Firebase for the transaction ID
      const paymentsRef = ref(database, "payments");
      const transactionQuery = query(
        paymentsRef,
        orderByChild("transactionId"),
        equalTo(transactionId)
      );

      const snapshot = await get(transactionQuery);

      // 2. Check if transaction exists and is completed
      if (snapshot.exists()) {
        const paymentData = Object.values(snapshot.val())[0];
        
        if (paymentData.status === "completed") {
          setPaymentStatus("success");
          
          // 3. Get form data (from location state or localStorage)
          const formData = location.state?.formData || 
                   JSON.parse(localStorage.getItem("healthCardFormData"));
          
          // 4. Add transaction ID to form data
          const cardData = {
            ...formData,
            transactionId: transactionId
          };
          
          // 5. Redirect to next page
          navigate("/generate-card", { state: { formData: cardData } });
        } else {
          setPaymentStatus("failed");
          setErrorMessage("Payment not completed yet.");
        }
      } else {
        setPaymentStatus("failed");
        setErrorMessage("Transaction ID not found.");
      }
    } catch (error) {
      console.error("Error:", error);
      setPaymentStatus("failed");
      setErrorMessage("Error verifying payment. Please try again.");
    } finally {
      setIsVerifying(false);
    }
  };

  // (Optional) Simulate a test payment (for development)
  const simulatePayment = async () => {
    setIsVerifying(true);
    setErrorMessage("");
    
    try {
      // Generate a random transaction ID (for testing)
      const simulatedTransactionId = `TXN${Math.floor(Math.random() * 1000000)}`;
      setTransactionId(simulatedTransactionId);
      
      // (Optional) Auto-verify after setting the ID
      await handleVerifyPayment();
    } catch (error) {
      console.error("Error:", error);
      setErrorMessage("Simulation failed.");
    } finally {
      setIsVerifying(false);
    }
  };

  return (
    <div className="payment-container">
      <h2>Scan & Pay</h2>
      <p>Scan the QR code below to complete the payment.</p>

      <img src={qrCodeImage} alt="UPI QR Code" className="qr-code" />

      <div className="input-section">
        <label>Enter Transaction ID:</label>
        <input
          type="text"
          placeholder="Enter UPI Transaction ID"
          value={transactionId}
          onChange={(e) => setTransactionId(e.target.value)}
          disabled={isVerifying}
        />
        
        <button 
          onClick={handleVerifyPayment} 
          className="verify-button"
          disabled={isVerifying}
        >
          {isVerifying ? "Verifying..." : "Verify Payment"}
        </button>

        {/* (Optional) For testing only */}
        <button 
          onClick={simulatePayment} 
          className="simulate-button"
          disabled={isVerifying}
        >
          {isVerifying ? "Processing..." : "Simulate Payment (Test)"}
        </button>
        
        {errorMessage && <p className="error-msg">{errorMessage}</p>}
        
        {paymentStatus === "success" && (
          <p className="success-msg">âœ… Payment Successful! Redirecting...</p>
        )}
      </div>
    </div>
  );
};

export default Payment;
