};

// Hospital data by region
const hospitalData = {
  "Narasaraopet": [
    "Narasaraopet General Hospital",
    "Sai Super Specialty Hospital",
    "Kamineni Hospitals Narasaraopet",
    "Guntur Medical College Branch"
  ],
  "Chilakaluripet": [
