    "Sathenapalli Government Hospital",
    "Sri Sai Hospital",
    "Vinayaka Hospital",
    "Ramesh Hospitals Branch"
  ],
  "Vinukonda": [
    "Vinukonda General Hospital",
    "NRI Medical College",
    "Palnadu Super Specialty Hospital",
    "Apollo Clinic"
  ],
  "Piduguralla": [
    "Piduguralla Community Hospital",
    "Sai Krishna Hospital",
    "Guntur General Hospital Branch",
    "Kamineni Primary Care"
  ]
};

// Service data
const serviceData = [
  { name: { en: "Medicine (Multispecialty)", te: "మందులు (మల్టీస్పెషాలిటీ)", hi: "दवाई (मल्टीस्पेशलिटी)" }, regularPrice: "5000-00", discountPrice: "4000-00" },
  { name: { en: "Blood Test", te: "రక్త పరీక్ష", hi: "रक्त परीक्षण" }, regularPrice: "1000-00", discountPrice: "800-00" },
  { name: { en: "Scanning", te: "స్కానింగ్", hi: "स्कैनिंग" }, regularPrice: "1000-00", discountPrice: "800-00" },
  { name: { en: "M.R.I. Scanning", te: "ఎమ్.ఆర్.ఐ. స్కానింగ్", hi: "एम.आर.आई. स्कैनिंग" }, regularPrice: "6000-00", discountPrice: "4800-00" },
  { name: { en: "Ultrasound", te: "అల్ట్రాసౌండ్", hi: "अल्ट्रासाउंड" }, regularPrice: "2400-00", discountPrice: "1800-00" },
  { name: { en: "X-Ray", te: "ఎక్స్-రే", hi: "एक्स-रे" }, regularPrice: "3500-00", discountPrice: "2800-00" },
  { name: { en: "I.C.U (1 day)", te: "ఐ.సి.యూ (1 రోజు)", hi: "आई.सी.यू (1 दिन)" }, regularPrice: "3500-00", discountPrice: "2800-00" },
  { name: { en: "General Ward (1 day)", te: "సాధారణ వార్డ్ (1 రోజు)", hi: "सामान्य वार्ड (1 दिन)" }, regularPrice: "2000-00", discountPrice: "1600-00" }
];

const GoogleLogin = () => {
  const [user, setUser] = useState(null);
  const [showRegions, setShowRegions] = useState(false);
  const [selectedRegion, setSelectedRegion] = useState(null);
  const [isMobile, setIsMobile] = useState(false);
  const [language, setLanguage] = useState("en");
  const [showLanguageDropdown, setShowLanguageDropdown] = useState(false);
  const [cardData, setCardData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
      if (user) {
        // Fetch card data when user is logged in
        const userRef = ref(database, `users/${user.uid}/card`);
        onValue(userRef, (snapshot) => {
          const data = snapshot.val();
          setCardData(data);
        });
      } else {
        setCardData(null);
      }
    });
    return () => unsubscribe();
  }, []);

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`${translations[language].welcome} ${result.user.displayName}`);
    } catch (error) {
      console.error("Google Sign-In Error:", error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      setSelectedRegion(null);
      alert(translations[language].loggedOut);
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  const toggleRegions = (e) => {
    e.stopPropagation();
    setShowRegions(!showRegions);
    if (!showRegions) {
      setSelectedRegion(null);
    }
  };

  const toggleLanguageDropdown = (e) => {
    e.stopPropagation();
    setShowLanguageDropdown(!showLanguageDropdown);
  };

  const selectRegion = (region) => {
    setSelectedRegion(region);
    setShowRegions(false);
  };

  const selectLanguage = (lang) => {
    setLanguage(lang);
    setShowLanguageDropdown(false);
  };

  const closeDropdowns = () => {
    setShowRegions(false);
    setShowLanguageDropdown(false);
  };

  useEffect(() => {
    document.addEventListener('click', closeDropdowns);
    return () => document.removeEventListener('click', closeDropdowns);
  }, []);

  const t = translations[language];

  return (
    <div style={styles.mainContainer}>
      <div style={styles.header}>
        <div style={styles.logoTitleContainer}>
          <img src={lgs} alt="Logo" style={styles.logo} />
          <h1 style={styles.title}>{t.title}</h1>
        </div>

        <div style={styles.buttonsContainer}>
          <div style={styles.languageDropdownContainer}>
            <button 
              onClick={toggleLanguageDropdown}
              style={{
                ...styles.languageButton,
                width: isMobile ? '100%' : 'auto'
              }}
            >
              {t.selectLanguage} {showLanguageDropdown ? '▲' : '▼'}
            </button>
            {showLanguageDropdown && (
              <div style={{
                ...styles.languageOptions,
                width: isMobile ? '100%' : 'auto'
              }}>
                <div 
                  style={styles.languageOption}
                  onClick={() => selectLanguage('en')}
                >
                  English
                </div>
                <div 
