              <h3 style={styles.userName}>{user.displayName}</h3>
              <p style={styles.userEmail}>{user.email}</p>
              <button 
                onClick={() => navigate("/healthcard")} 
                style={styles.cardButton}
              >
                Happy Doctors Card
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

