import React, { useState } from "react";
import { auth, provider, signInWithPopup, signOut } from "../firebase";
import { useNavigate } from "react-router-dom";
import lgs from "../assets/lgs.jpg";

const GoogleLogin = () => {
  const [user, setUser] = useState(null);
  const [showHospitals, setShowHospitals] = useState(false);
  const navigate = useNavigate();

  const hospitals = [
    "Guntur General Hospital",
    "Palnadu Super Specialty Hospital",
    "NRI Medical College",
    "Kamineni Hospitals",
    "Sai Priya Hospital",
    "Sri Sai Hospital",
    "Life Care Hospital"
  ];

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`Welcome ${result.user.displayName}`);
    } catch (error) {
      console.error("Google Sign-In Error:", error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      alert("Logged out successfully!");
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  const toggleHospitals = () => {
    setShowHospitals(!showHospitals);
  };

  return (
    <div style={styles.mainContainer}>
      <div style={styles.header}>
        <div style={styles.leftSection}>
          <img src={lgs} alt="Logo" style={styles.logo} />
          <h1 style={styles.title}>Happy Doctors Consultancy</h1>
        </div>

        <div style={styles.rightSection}>
          <div style={styles.dropdownWrapper}>
            <button onClick={toggleHospitals} style={styles.hospitalsButton}>
              Hospitals
            </button>
            {showHospitals && (
              <div style={styles.dropdownContent}>
                {hospitals.map((hospital, index) => (
                  <div key={index} style={styles.hospitalItem}>{hospital}</div>
                ))}
              </div>
            )}
          </div>
          
          {!user ? (
            <button onClick={signInWithGoogle} style={styles.button}>
              Sign in
            </button>
          ) : (
            <button onClick={handleLogout} style={styles.button}>
              Logout
            </button>
          )}
        </div>
      </div>

      {user && (
        <div style={styles.profileContainer}>
          <div style={styles.profile}>
            <img src={user.photoURL} alt="Profile" style={styles.avatar} />
            <h3>{user.displayName}</h3>
            <p>{user.email}</p>
            <button 
              onClick={() => navigate("/healthcard")} 
              style={styles.cardButton}
            >
              Happy Doctors Card
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  mainContainer: {
    width: "100%",
    padding: "10px",
    boxSizing: "border-box",
  },

  header: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    width: "100%",
    padding: "10px 15px",
    backgroundColor: "lightgreen",
    borderRadius: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    flexWrap: "nowrap",
    overflow: "hidden",
  },

  leftSection: {
    display: "flex",
    alignItems: "center",
    gap: "15px",
    minWidth: 0,
    flex: 1,
  },

  rightSection: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    flexShrink: 0,
  },

  logo: {
    width: "50px",
    height: "50px",
    borderRadius: "50%",
    objectFit: "cover",
    flexShrink: 0,
  },

  title: {
    fontSize: "clamp(16px, 2vw, 22px)",
    fontWeight: "bold",
    margin: 0,
    whiteSpace: "nowrap",
    overflow: "hidden",
    textOverflow: "ellipsis",
    flex: 1,
    minWidth: 0,
  },

  dropdownWrapper: {
    position: "relative",
  },

  hospitalsButton: {
    backgroundColor: "#6c757d",
    color: "white",
    padding: "8px 12px",
    fontSize: "14px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    whiteSpace: "nowrap",
  },

  dropdownContent: {
    position: "absolute",
    right: 0,
    backgroundColor: "#f9f9f9",
    minWidth: "200px",
    boxShadow: "0px 8px 16px 0px rgba(0,0,0,0.2)",
    zIndex: 1,
    borderRadius: "5px",
    maxHeight: "300px",
    overflowY: "auto",
  },

  hospitalItem: {
    color: "black",
    padding: "12px 16px",
    textDecoration: "none",
    display: "block",
    borderBottom: "1px solid #ddd",
    "&:hover": {
      backgroundColor: "#f1f1f1"
    }
  },

  button: {
    backgroundColor: "#4285F4",
    color: "white",
    padding: "8px 12px",
    fontSize: "14px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    whiteSpace: "nowrap",
  },

  profileContainer: {
    width: "100%",
    textAlign: "center",
    marginTop: "20px",
  },

  profile: {
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    display: "inline-block",
    maxWidth: "90%",
    boxSizing: "border-box",
  },

  avatar: {
    width: "80px",
    borderRadius: "50%",
  },

  cardButton: {
    backgroundColor: "green",
    color: "white",
    padding: "10px 15px",
    fontSize: "14px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
    width: "100%",
  },

  "@media (max-width: 768px)": {
    header: {
      padding: "8px 10px",
    },
    
    title: {
      fontSize: "18px",
    },
    
    logo: {
      width: "40px",
      height: "40px",
    },
    
    hospitalsButton: {
      padding: "6px 10px",
      fontSize: "12px",
    },
    
    button: {
      padding: "6px 10px",
      fontSize: "12px",
    }
  },

  "@media (max-width: 480px)": {
    title: {
      fontSize: "16px",
    },
    
    leftSection: {
      gap: "8px",
    },
    
    rightSection: {
      gap: "5px",
    }
  }
};

export default GoogleLogin;
