import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Barcode from "react-barcode";
import "./GenerateCard.css";
import companyLogo from "./logo.jpg"; // Import your logo image

const GenerateCard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoading, setIsLoading] = useState(false);
  const [storageStatus, setStorageStatus] = useState(null);
  const [cardData, setCardData] = useState({
    snfNo: "N/A",
    cardHolderName: "Not Available",
    mobile: "Not Available",
    address: "Not Available",
    familyDetails: []
  });

  // Calculate dates
  const issueDate = new Date();
  const expiryDate = new Date();
  expiryDate.setFullYear(expiryDate.getFullYear() + 1);

  const formatDate = (date) => date.toISOString().split("T")[0];

  // Generate barcode value (SNF + last 4 digits of mobile)
  const generateBarcodeValue = (data) => {
    const mobileDigits = data.mobile.slice(-4).padStart(4, '0');
    return `${data.snfNo}-${mobileDigits}`;
  };

  // Initialize card data
  useEffect(() => {
    const formData = location.state?.formData || 
                    JSON.parse(localStorage.getItem("healthCardFormData"));
    
    if (formData) {
      const completeCardData = {
        ...formData,
        issueDate: formatDate(issueDate),
        expiryDate: formatDate(expiryDate),
        barcode: generateBarcodeValue(formData)
      };
      setCardData(completeCardData);
      storeCardData(completeCardData);
    }
  }, [location.state]);

  const storeCardData = async (cardData) => {
    setIsLoading(true);
    setStorageStatus(null);
    
    try {
      const response = await fetch("http://localhost:5000/store-card", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ cardData }),
      });

      const data = await response.json();
      if (data.success) {
        setStorageStatus("success");
        localStorage.removeItem("healthCardFormData");
      } else {
        setStorageStatus("error");
        console.error("Failed to store health card:", data.message);
      }
    } catch (error) {
      setStorageStatus("error");
      console.error("Error storing health card:", error);
      // Fallback to localStorage if API fails
      localStorage.setItem("healthCardFormData", JSON.stringify(cardData));
    } finally {
      setIsLoading(false);
    }
  };

  if (!cardData) {
    return (
      <div className="card-wrapper">
        <h2>Error: No card data found</h2>
        <p>Please go back and complete the form again.</p>
        <button className="home-btn" onClick={() => navigate("/")}>
          🏠 Go to Home
        </button>
      </div>
    );
  }

  return (
    <div className="card-wrapper">
      <div className="card-container">
        {/* Front Side of the Card */}
        <div className="card front">
          <div className="card-header">
            <div className="company-logo-container">
              <img src={companyLogo} alt="Happy Doctors Consultancy" className="company-logo" />
            </div>
            <h2 className="company-name">Happy Doctors Consultancy</h2>
            <h3 className="card-number">Card No: {cardData.snfNo}</h3>
          </div>
          <div className="card-content">
            <div className="card-field">
              <span className="field-label">Card Holder:</span>
              <span className="field-value">{cardData.cardHolderName}</span>
            </div>
            <div className="card-field">
              <span className="field-label">Phone:</span>
              <span className="field-value">{cardData.mobile}</span>
            </div>
            <div className="card-field">
              <span className="field-label">Address:</span>
              <span className="field-value">{cardData.address}</span>
            </div>
            <div className="card-field">
              <span className="field-label">Issued Date:</span>
              <span className="field-value">{cardData.issueDate}</span>
            </div>
            <div className="card-field">
              <span className="field-label">Valid Until:</span>
              <span className="field-value">{cardData.expiryDate}</span>
            </div>
            
            {/* Barcode */}
            <div className="barcode-container">
              <Barcode
                value={cardData.barcode || generateBarcodeValue(cardData)}
                format="CODE128"
                width={1.5}
                height={50}
                displayValue={true}
              />
            </div>
          </div>
        </div>

        {/* Back Side of the Card */}
        <div className="card back">
          <div className="card-header">
            <h2 className="company-name">Happy Doctors Consultancy</h2>
            <h4>Family Members</h4>
          </div>
          <div className="card-content">
            <ul className="family-list">
              {cardData.familyDetails.map((member, index) => (
                <li key={index}>
                  <strong>{member.name}</strong> - {member.age} years old ({member.relation})
                </li>
              ))}
            </ul>
          </div>
          <div className="card-footer">
            <p>Emergency Contact: {cardData.mobile}</p>
            <div className="signature-area">
              <div className="signature-box">
                <p>Card Holder Signature</p>
              </div>
              <div className="signature-box">
                <p>Approved Signature</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="action-buttons">
        <button 
          className="print-btn"
          onClick={() => window.print()}
        >
          🖨️ Print Card
        </button>
        <button 
          className="home-btn" 
          onClick={() => navigate("/")}
        >
          🏠 Go to Home
        </button>
      </div>
    </div>
  );
};

export default GenerateCard;
