import React, { useState } from "react";
import "./Payment.css";
import { useNavigate, useLocation } from "react-router-dom";
import { database, ref, set } from "../firebase"; // Your Firebase config

const Payment = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [paymentStatus, setPaymentStatus] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");

  const handleRazorpayPayment = async () => {
    const amount = 100; // Rs.100
    try {
      const res = await fetch("http://localhost:5000/create-order", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ amount }),
      });

      const data = await res.json();
      if (!data.success) throw new Error("Failed to create Razorpay order");

      const options = {
        key: "your_razorpay_key_id", // Replace with Razorpay public key
        amount: data.order.amount,
        currency: "INR",
        name: "Happy Doctors Consultancy",
        description: "Health Card Purchase",
        order_id: data.order.id,
        handler: async function (response) {
          const transactionId = response.razorpay_payment_id;

          // ✅ Save to Firebase
          const paymentRef = ref(database, `payments/${transactionId}`);
          await set(paymentRef, {
            transactionId,
            status: "completed",
            amount,
            time: new Date().toISOString()
          });

          setPaymentStatus("success");

          const formData = location.state?.formData ||
            JSON.parse(localStorage.getItem("healthCardFormData"));

          const cardData = {
            ...formData,
            transactionId
          };

          navigate("/generate-card", { state: { formData: cardData } });
        },
        prefill: {
          name: "Satish Kumar",
          email: "satish@example.com",
          contact: "9999999999"
        },
        theme: {
          color: "#4CAF50"
        }
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (err) {
      console.error(err);
      setErrorMessage("Payment initiation failed");
    }
  };

  return (
    <div className="payment-container">
      <h2>Pay & Generate Card</h2>
      <p>Click below to make payment securely via Razorpay.</p>

      <button 
        onClick={handleRazorpayPayment} 
        className="verify-button"
      >
        Pay with Razorpay
      </button>

      {errorMessage && <p className="error-msg">{errorMessage}</p>}

      {paymentStatus === "success" && (
        <p className="success-msg">✅ Payment Successful! Redirecting...</p>
      )}
    </div>
  );
};

export default Payment;
