import React, { useEffect, useState, useRef } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Barcode from "react-barcode";
import "./GenerateCard.css";
import logo from "../assets/logo.png";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";

const GenerateCard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const frontRef = useRef(null);
  const backRef = useRef(null);

  const [isLoading, setIsLoading] = useState(false);
  const [storageStatus, setStorageStatus] = useState(null);
  const [cardData, setCardData] = useState({
    snfNo: "N/A",
    cardHolderName: "Not Available",
    mobile: "Not Available",
    address: "Not Available",
    familyDetails: []
  });

  const [flipped, setFlipped] = useState(false);

  const downloadPDF = async () => {
    setIsLoading(true);
    
    try {
      // Create PDF in landscape mode (A4 size)
      const pdf = new jsPDF("l", "mm", "a4");
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = pdf.internal.pageSize.getHeight();

      // Standard credit card dimensions (85.6mm √ó 53.98mm)
      const cardWidth = 85.6;
      const cardHeight = 53.98;
      
      // Calculate positions - centered with gap
      const frontX = (pdfWidth/2 - cardWidth - 5);
      const frontY = (pdfHeight - cardHeight)/2;
      const backX = (pdfWidth/2 + 5);
      const backY = (pdfHeight - cardHeight)/2;

      // Capture front side
      const frontCanvas = await html2canvas(frontRef.current, {
        scale: 3,
        logging: true,
        useCORS: true,
        backgroundColor: null,
        allowTaint: true
      });
      
      // Capture back side
      const backCanvas = await html2canvas(backRef.current, {
        scale: 3,
        logging: true,
        useCORS: true,
        backgroundColor: null,
        allowTaint: true
      });

      // Add images to PDF
      pdf.addImage(frontCanvas, 'PNG', frontX, frontY, cardWidth, cardHeight);
      pdf.addImage(backCanvas, 'PNG', backX, backY, cardWidth, cardHeight);

      // Add footer
      pdf.setFontSize(10);
      pdf.setTextColor(150);
      pdf.text("Generated by Happy Doctors Consultancy", pdfWidth/2, pdfHeight - 10, {align: 'center'});

      pdf.save("HappyDoctorsCard.pdf");
      
    } catch (error) {
      console.error("Error generating PDF:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // Calculate dates
  const issueDate = new Date();
  const expiryDate = new Date();
  expiryDate.setFullYear(expiryDate.getFullYear() + 1);

  const formatDate = (date) => date.toISOString().split("T")[0];

  const generateBarcodeValue = (data) => {
    const mobileDigits = data.mobile.slice(-4).padStart(4, '0');
    return `${data.snfNo}-${mobileDigits}`;
  };

  useEffect(() => {
    const formData = location.state?.formData || 
                    JSON.parse(localStorage.getItem("healthCardFormData"));
    
    if (formData) {
      const completeCardData = {
        ...formData,
        issueDate: formatDate(issueDate),
        expiryDate: formatDate(expiryDate),
        barcode: generateBarcodeValue(formData)
      };
      setCardData(completeCardData);
      storeCardData(completeCardData);
    }
  }, [location.state]);

  const storeCardData = async (cardData) => {
    setIsLoading(true);
    setStorageStatus(null);
    
    try {
      const response = await fetch("http://localhost:5000/store-card", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ cardData }),
      });

      const data = await response.json();
      if (data.success) {
        setStorageStatus("success");
        localStorage.removeItem("healthCardFormData");
      } else {
        setStorageStatus("error");
        console.error("Failed to store health card:", data.message);
      }
    } catch (error) {
      setStorageStatus("error");
      console.error("Error storing health card:", error);
      localStorage.setItem("healthCardFormData", JSON.stringify(cardData));
    } finally {
      setIsLoading(false);
    }
  };

  if (!cardData) {
    return (
      <div className="card-wrapper">
        <h2>Error: No card data found</h2>
        <p>Please go back and complete the form again.</p>
        <button className="home-btn" onClick={() => navigate("/")}>
          üè† Go to Home
        </button>
      </div>
    );
  }

  return (
    <div className="card-wrapper">
      <h2 className="title">üéâ Happy Doctors Consultancy Card üéâ</h2>
      
      {isLoading && <div className="loading">Generating PDF...</div>}
      {storageStatus === "success" && (
        <div className="success-message">
          ‚úÖ Card data saved successfully!
        </div>
      )}
      {storageStatus === "error" && (
        <div className="error-message">
          ‚ùå Failed to save card data. Please contact support.
        </div>
      )}

      <div className={`card-container ${flipped ? "flip" : ""}`} onClick={() => setFlipped(!flipped)}>
        {/* Front Side */}
        <div className="card front" ref={frontRef}>
          <div className="card-header">
            <img src={logo} alt="Company Logo" className="card-logo" />
            <div className="header-text">
              <h2>HAPPY DOCTORS CONSULTANCY</h2>
              <p>Cell: {cardData.mobile}</p>
            </div>
          </div>
          <div className="card-content">
            <p><strong>S.N.F. No.:</strong> {cardData.snfNo}</p>
            <p><strong>Card Holder:</strong> {cardData.cardHolderName}</p>
            <p><strong>Phone:</strong> {cardData.mobile}</p>
            <p><strong>Address:</strong> {cardData.address}</p>
            <p><strong>Issued Date:</strong> {cardData.issueDate}</p>
            <p><strong>Valid Until:</strong> {cardData.expiryDate}</p>
            
            <div className="barcode-container">
              <Barcode
                value={cardData.barcode || generateBarcodeValue(cardData)}
                format="CODE128"
                width={1.5}
                height={50}
                displayValue={true}
              />
            </div>
          </div>
        </div>
        
        {/* Back Side */}
        <div className="card back" ref={backRef}>
          <div className="card-header">
            <img src={logo} alt="Company Logo" className="card-logo" />
            <div className="header-text">
              <h2>HAPPY DOCTORS CONSULTANCY</h2>
              <p>Family Members Details</p>
            </div>
          </div>
          <div className="card-content">
            <table className="family-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Relation</th>
                </tr>
              </thead>
              <tbody>
                {cardData.familyDetails.map((member, index) => (
                  <tr key={index}>
                    <td>{member.name}</td>
                    <td>{member.age}</td>
                    <td>{member.relation}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="card-footer">
            <p>Emergency Contact: {cardData.mobile}</p>
          </div>
        </div>
      </div>

      <div className="action-buttons">
        <button className="print-btn" onClick={downloadPDF}>
          üì• Download PDF
        </button>
        <button className="print-btn" onClick={() => window.print()}>
          üñ®Ô∏è Print Card
        </button>
        <button className="home-btn" onClick={() => navigate("/")}>
