  cardButton: {
    backgroundColor: '#34a853',
    color: 'white',
    padding: '12px 25px',
    fontSize: '1rem',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    width: '100%',
    maxWidth: '300px',
    margin: '0 auto',
    display: 'block',
  },

  '@media (max-width: 768px)': {
    header: {
      flexDirection: 'column',
